module (..., package.seeall )function new()local localGroup=display.newGroup ( )local RTouch = 10 -- радиус круга в которой должен попасть человек , при прикосновении		local background=display.newImage("image/bg.png")  background.x= display.viewableContentWidth/2    background.y=display.contentHeight/2    background.xScale = display.viewableContentWidth/background.contentWidth     background.yScale = display.viewableContentHeight/background.contentHeightlocalGroup:insert(background)	local title=display.newText("Touch the north, then to the south twice", 50, 20, native.systemFont, 16 ) title.x= display.contentWidth/2title.y= (display.contentHeight - display.viewableContentHeight)/2 + title.contentHeight*1/2title:setTextColor ( 50, 50, 255)localGroup:insert(title)		local pic3= display.newImage("image/5/level3/compass.jpg")pic3.xScale = 1.5pic3.yScale = 1.5pic3.x= display.viewableContentWidth/2pic3.y= display.contentHeight/2 localGroup:insert(pic3)local flagN = falselocal flagS = falselocal flagSS = falselocal function done(event)if event.phase=="ended" then 	 	 local flag = false	 title.alpha = 0	 print (pic3.contentHeight/4.29)	 	 if event.x < pic3.x + RTouch  and event.x > pic3.x - RTouch       and event.y < pic3.y - pic3.contentHeight/2 + pic3.contentHeight/17.17+ RTouch  and event.y > pic3.y - pic3.contentHeight/2 + pic3.contentHeight/17.17 - RTouch	 and flagN == false and flagS == false and flagSS == false and flag == false	 then	  flag = true	  flagN = true	 end	 	  if event.x < pic3.x + RTouch  and event.x > pic3.x - RTouch       and event.y < pic3.y + pic3.contentHeight/2 - pic3.contentHeight/17.17 + RTouch  and event.y > pic3.y - pic3.contentHeight/17.17 + pic3.contentHeight/2 - RTouch	 and flagN == true and flagS == false and flagSS == false and flag == false	 then	  flag = true	  flagS = true	  pic3.rotation= 180	 end	 	 if event.x < pic3.x + RTouch  and event.x > pic3.x - RTouch       and event.y < pic3.y - pic3.contentHeight/2 + pic3.contentHeight/17.17 + RTouch  and event.y > pic3.y - pic3.contentHeight/2 + pic3.contentHeight/17.17 - RTouch	 and flagN == true and flagS == true and flagSS == false and flag == false	 then	  flag = true	  flagSS = true	  director:changeScene("s1r5l61")	 end	 	 if flag == false and flagSS ~= true then		print ("fail")	  director:changeScene("fail")	endendend background:addEventListener ( "touch", done )	return localGroupend