module (..., package.seeall )function new()	local localGroup=display.newGroup ( )			local background=display.newImage("image/bg.png")  background.x= display.viewableContentWidth/2    background.y=display.contentHeight/2    background.xScale = display.viewableContentWidth/background.contentWidth     background.yScale = display.viewableContentHeight/background.contentHeightlocalGroup:insert(background)	local title=display.newText("Нажми правую кнопку мыши", 50, 20, native.systemFont, 17 ) title.x= title.contentWidth/2title.y= (display.contentHeight - display.viewableContentHeight)/2 + title.contentHeight*1/2title:setTextColor ( 50, 50, 255)localGroup:insert(title)	local pic3 = display.newImageRect("image/5/level5/mous.jpg",50, 50)pic3.xScale = 3pic3.yScale = 3.5pic3.x = display.viewableContentWidth*1/2pic3.y = display.contentHeight/2pic3.rotation= 180localGroup:insert(pic3)local function done(event)if event.phase=="ended" then 	 if event.x <= pic3.x + pic3.contentWidth/2 - (75/111)*pic3.contentWidth and event.x >= pic3.x + pic3.contentWidth/2 -(110/111)*pic3.contentWidth     and event.y >= pic3.y + pic3.contentHeight/2 -(95/220)*pic3.contentHeight and event.y <= pic3.y + pic3.contentHeight/2 -(40/220)*pic3.contentHeight  then director:changeScene("s1r5l62") else print ("fail") director:changeScene("fail") endendendbackground:addEventListener ( "touch", done )	return localGroupend