module (..., package.seeall )function new()	local localGroup=display.newGroup ( )			local background=display.newImage("image/bg.png")  background.x= display.viewableContentWidth/2    background.y=display.contentHeight/2    background.xScale = display.viewableContentWidth/background.contentWidth     background.yScale = display.viewableContentHeight/background.contentHeightlocalGroup:insert(background)	local title=display.newText("Нажми на красную кнопку десять раз", 50, 20, native.systemFont, 17 ) title.x= display.contentWidth/2title.y= (display.contentHeight - display.viewableContentHeight)/2 + title.contentHeight*1/2title:setTextColor ( 50, 50, 255)localGroup:insert(title)	local pic3 = display.newImageRect("image/6/level1/r.jpg",50, 50)pic3.xScale = 2pic3.yScale = 2pic3.x = display.viewableContentWidth*1/2pic3.y = display.contentHeight/2pic3.isVisible = truelocalGroup:insert(pic3)local button = display.newImageRect("image/6/level1/grean.png",50, 50)button.xScale = 2button.yScale = 2button.x = display.viewableContentWidth*1/2 +50button.y = display.contentHeight/2button.isVisible = falselocalGroup:insert(button)local n =0local flag = falselocal function ololo (event)     pic3.isVisible = true	 button.isVisible = false	 flag = falseendlocal function done(event)if event.phase=="ended" then 	 title.isVisible = false   if flag ~=false then print ("fail") director:changeScene("fail") end  if (event.x - pic3.x)^2 + (event.y - pic3.y)^2 <= (pic3.contentHeight^2)/4  and flag == false then  n=n+1   if n==4 then   pic3.x = math.random(pic3.contentWidth/2,display.viewableContentWidth - pic3.contentHeight/2)   pic3.y = math.random(pic3.contentHeight/2,display.viewableContentHeight*1/2+display.contentHeight*1/2 - pic3.contentHeight/2)   end      if n==7 then       pic3.isVisible = false	 button.isVisible = true     button.x = pic3.x   	 button.y = pic3.y	 flag =  true	 timer.performWithDelay( 5000, ololo, 1 )   end      if n == 10 then director:changeScene("s1r6l2") end else  print ("fail") director:changeScene("fail") endendendbackground:addEventListener ( "touch", done )	return localGroupend